import { Container } from '@mui/system'
import { Button, TextField } from '@mui/material';
import { useState } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import { useEffect } from "react";

function Movies() {
  const navigate = useNavigate();

  useEffect(() => {
    navigate('moviesData')
  }, [])

  const [findMovie, setFindMovie] = useState('');

  const FindMovie = async (e) => {
    e.preventDefault();
    //find movie and display

  }
  return (
    <Container >
      <h3>Movies</h3>
      <Button sx={{ padding: 2, marginTop: 2 }} variant="outlined" size="small" type='submit' color="primary" onClick={() => navigate(`moviesData`)}>All Movies</Button>
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type='submit' color="primary" onClick={() => navigate(`/mainPage/movies/addNewMovie`)}>Add Movie</Button>
      <TextField sx={{ padding: 1, marginTop: 1, marginLeft: 1 }} required label="Find Movie" id="outlined-find-movie" onChange={(e) => setFindMovie(e.target.value)} />
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type='submit' color="primary" onClick={FindMovie}>Find</Button><br />
      <Outlet />
    </Container>
  );
}

export default Movies;
