import { Container } from '@mui/system'
import { Button, FormControl, InputLabel, Select, OutlinedInput, MenuItem } from '@mui/material';
import { useLocation } from 'react-router-dom';
import { useState } from 'react';
import { useSelector } from 'react-redux';

function SubscribeToNewMovie() {
  const location = useLocation()
  const member = location.state.member;
  const movies = useSelector((state) => state.movies);
  const [movieData, setMovieData] = useState({ memberId: member._id, movies: [{ movieId: String, date: Date }] });
  const [newMovieId, setNewMovieId] = useState('');
  const [newDate, setNewDate] = useState('');


  const handleMovieSelect = (movieId, date) => {
    // Convert the date string to a Date object
    const formattedDate = new Date(newDate);

    // Create a new movie object with the selected movieId and formattedDate
    const newMovie = { newMovieId, date: formattedDate };

    // Update the movieData state by creating a new array that includes the new movie object
    setMovieData(prevState => {
      return { ...prevState, movies: [...prevState.movies, newMovie] };
    });
    console.log(movieData)
  };







  const handleSubmit = async (e) => {
    e.preventDefault();
    //Save movie to member subscriptions with member_ID in DB and make MSG
    console.log(movieData)

  }
  return (
    <Container>
      <br />
      <h3>Add New Movie</h3>

      <FormControl required sx={{ m: 1, width: 300, }}>
        <InputLabel id="demo-simple-select-required-label" >Movies</InputLabel>
        <Select labelId="demo-multiple-movies-label" id="demo-multiple-movies"
          defaultValue='' name='movieId' onChange={(e) => setNewMovieId(e.target.value)} input={<OutlinedInput label="Movies" />}>
          {
            movies.map(movie => {
              return <MenuItem value={movie._id} key={movie._id}>{movie.name}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      <br />
      <input style={{ padding: 18.5, marginTop: 8, width: 260, borderColor: 'lightgrey ', borderWidth: 1, borderRadius: 5 }}
       defaultValue=' ' type='date' name='date'  onChange={(e) => setNewDate(e.target.value)} /><br /><br />
      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={handleMovieSelect}>Subscribe</Button>

    </Container>
  );
}

export default SubscribeToNewMovie;
