import { Container } from '@mui/system'
import { Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'

function UserData({ person }) {
  const navigate = useNavigate();
  const permissions = person.permissions;
  const permissionsString = permissions.join(", ");
  const DeleteUser = async (e) => {

    //delete in DB 'http://localhost:8080/users'
    const resp = await axios.delete(`${'http://localhost:8080/users'}/${person.id}`);
    window.location.reload();
  }
  return (
    <Container sx={{ marginTop: 2 }}>
      Full Name: {person.firstName + ' ' + person.lastName}<br /><br />
      User Name: {person.username}<br /><br />
      Session time out:{person.sessionTimeOut}<br /><br />
      Created data:{person.createdData}<br /><br />
      Permissions: {permissionsString}<br /><br />
      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/usersManagement/editUser', { state: { person } })}>Edit</Button>
      <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={DeleteUser}>Delete</Button>
    </Container>
  );
}

export default UserData;
