import { Container } from '@mui/system'
import { Button, Card, CardMedia, CardContent } from '@mui/material';
import { useNavigate, Link } from 'react-router-dom';


function MovieData({ movie }) {
  const navigate = useNavigate();
  const date = new Date(movie.premiered);
  const year = date.getFullYear();
  const genres = movie.genres;
  const genresString = genres.join(", ");

  const DeleteMovie = async (e) => {
    e.preventDefault();
    //delete movie in DB

  }
  return (
    <Container>
      <br />
      <h2>{movie.name} , {year}</h2>
      genres: {genresString}<br />
      <Container><img src={movie.image} alt="pic" /></Container>

      <Card sx={{ width: 150, padding: 1, margin: 2, background: '#eeeeee' }} elevation={3}>
        <CardMedia height="150">
          </CardMedia> <CardContent>
        </CardContent>
      </Card>



      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/usersManagement/editUser', { state: { movie } })}>Edit</Button>
      <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={DeleteMovie}>Delete</Button>
    </Container>
  );
}

export default MovieData;
