const initialValue = {
  movies: [],
  members: [],
  subscriptions: [],
  users: [],
};

const storeReducer = (state = initialValue, action) => {
  switch (action.type) {
    case 'LOADMOVIES':
      {
        const movies = JSON.stringify({ ...state, movies: action.payload });
        sessionStorage.setItem('movies', movies);
        return { ...state, movies: action.payload };
      }
    case 'ADDMOVIE':
      {
        const movie = action.payload;
        const updatedMovies = [...state.movies, movie];
        return { ...state, movies: updatedMovies };
      }
    case 'DELETE_MOVIE':
      const movieId = action.payload;
      const updatedMovies = state.movies.filter(movie => movie.id !== movieId);
      return { ...state, movies: updatedMovies };
    case 'LOADMEMBERS': {
      const members = JSON.stringify({ ...state, members: action.payload });
      sessionStorage.setItem('members', members);
      return { ...state, members: action.payload };
    }
    case 'LOADSUBSCRIPTIONS': {
      const subscriptions = JSON.stringify({ ...state, subscriptions: action.payload });
      sessionStorage.setItem('subscriptions', subscriptions);
      return { ...state, subscriptions: action.payload };
    }
    case 'LOADUSERS':
      return { ...state, users: action.payload };
    default:
      return state;
  }
};

export default storeReducer;
