import { Container } from "@mui/system";
import { Button, TextField,Card } from "@mui/material";
import { useState } from "react";
import { useNavigate, Outlet } from "react-router-dom";
import { useSelector } from "react-redux";
import { Grid } from "@mui/material";
import MovieData from "./MovieData";
function Movies() {
  const navigate = useNavigate();
  const movies = useSelector((state) => state.movies);
  const [findMovie, setFindMovie] = useState("");
  const [moviesToShow, setMoviesToShow] = useState(movies);

  const FindMovie = (e) => {
    // find movie and display
    try {
      if (findMovie) {
        const searchString = findMovie.trim();
        const filteredMovies = movies.filter((mov) => mov.name.toLowerCase().includes(searchString.toLowerCase())
      );
        setMoviesToShow(filteredMovies); // update state with filtered movies
      } else {
        setMoviesToShow(movies); // update state with all movies
      }
    } catch (error) {
      console.log(error);
    }
  };
  const AllMoviesShow= (e)=>{
    setMoviesToShow(movies);
  };
  return (
    <Container>
      <h3>MOVIES</h3>
      <Button sx={{ padding: 2, marginTop: 2 }} variant="outlined" size="small" type="submit" color="primary"
      onClick={AllMoviesShow}>All Movies</Button>
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type="submit" color="primary"
      onClick={() => navigate(`/mainPage/addNewMovie`)}>Add Movie</Button>
      <TextField sx={{ padding: 1, marginTop: 1, marginLeft: 1 }} required label="Find Movie" id="outlined-find-movie"
      onChange={(e) => setFindMovie(e.target.value)}/>
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type="submit" color="primary"
      onClick={FindMovie}>{" "}Find{" "}</Button>
      <br />
      <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
      <Card sx={{ padding: 1, marginLeft: 2, background: '#eeeeee' }} elevation={3}>
      <ul>
        {

        moviesToShow.map((mov) => {
          return <li key={mov._id}>
            <ul>
              <li> {mov.name}</li><br/>
              <li> {mov.premiered}</li>
              <li> {mov.genres}</li>
              <li> {<img src={mov.image} alt="pic" />}</li>
            </ul>
          </li>
          })
        }
      </ul>
      </Card>
      </Grid>
      <Outlet />
    </Container>
  );
}
export default Movies;
