import { Container } from "@mui/system";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate, Outlet } from "react-router-dom";
import {AppBar,Button,IconButton,Toolbar,Typography,Avatar,Stack,} from "@mui/material";
import axios from "axios";
import { useSelector } from "react-redux";

function MainPage() {
  const users = useSelector((state) => state.users);// all users here
  const username = sessionStorage.getItem("username");
  const userOnLine = users.find(person=>person.username===username)

  const [user, setUser] = useState({});
  const [usersManagementVisible, setUsersManagementVisible] = useState(false);
  const [subscriptionsVisible, setSubscriptionsVisible] = useState(false);
  const [moviesVisible, setMoviesVisible] = useState(false);
  const [timeLeft, setTimeLeft] = useState();
  const navigate = useNavigate();
  


  useEffect(() => {
    const UsersData = async () => {
      try {
        const resp = await axios.get(`http://localhost:8080/users/${username}`);
        setUser(userOnLine);
        setTimeLeft(userOnLine.sessionTimeOut*60)
        const userStr = JSON.stringify(userOnLine);
        sessionStorage.setItem('userOnline', userStr);
        if (userOnLine.role === "admin") {
          setUsersManagementVisible(true);
          setSubscriptionsVisible(true);
          setMoviesVisible(true);
        }
        else{
        if(userOnLine.permissions.includes("View Subscriptions"))
        {
          setSubscriptionsVisible(true);
        }
        if(userOnLine.permissions.includes("View Movies"))
        {
          setMoviesVisible(true);
        }
      }
      } catch (error) {
        console.error("Error: ", error);
      }
    };
    UsersData();
    
  }, []);
// Timer
  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(prevTimeLeft => prevTimeLeft - 1);
      console.log('timeLeft:' + timeLeft)
    }, 1000);
    if (timeLeft === 0) {
      clearTimeout(timer);
      logOutSubmit();
    }
    return () => clearTimeout(timer);
  }, [timeLeft]);
 
  
  const logOutSubmit = async (e) => {
    navigate(`/`);
  };
  return (
    <Container>
      <AppBar position="static" color="default">
        <Toolbar>
          <IconButton size="small" edge="start" color="inherit" aria-label="menu" sx={{ mr: 70 }} disabled >
            <h5>Movies-Subscriptions Web Site </h5>
          </IconButton>
          <Stack direction="row" spacing={2}>
            <Avatar src="/broken-image.jpg" />
            <h3>{user.firstName + " " + user.lastName}</h3>
            <Button sx={{ padding: 1, marginTop: 1 }} variant="outlined" size="small" type="submit" color="primary" onClick={logOutSubmit} >Log Out</Button><br/>
          </Stack>
        </Toolbar>
        <Typography variant="h6" component="div" sx={{ flexGrow: 0.1, borderColor: "divider" }} >
          {moviesVisible && <Button onClick={() => navigate(`movies`)}>Movies</Button>}
          {subscriptionsVisible && <Button onClick={() => navigate(`/mainPage/subscriptions/membersData`)}>Subscriptions</Button>}
          {usersManagementVisible && <Button onClick={() => navigate(`/mainPage/usersManagement/usersData`)}>Users Management</Button>}
          <Button sx={{ paddingLeft:"430px"}} disabled onClick={() => navigate(`/mainPage/usersManagement/usersData`)}>Expiration time:{timeLeft} seconds</Button>
        </Typography>
        
      </AppBar>
      <Outlet />
    </Container>
  );
}
export default MainPage;
