import { Container } from '@mui/system'
import { Button, TextField } from '@mui/material';
import { useLocation, useNavigate} from 'react-router-dom';
import { useState } from 'react';

function EditUser({person}) {
  const navigate = useNavigate();
  const location = useLocation()
  const User = location.state
  const [userData, setUserData] = useState({ firstName: String(User.firstName), lastName: String(User.lastName)});

  const UpdateUser = async (e) => {
    e.preventDefault();
   //update in DB

  }

  return (
    <Container>
      <br/>

      <TextField required label=" First Name" id="outlined-first-name" defaultValue={User.firstName} onChange={(e) => setUserData({ ...userData, firstName: e.target.value })}/>
      Full Name: {person.firstName +' '+ person.lastName}<br/><br/>
      User Name: {person.username}<br/><br/>
      Session time out:{person.sessionTimeOut}<br/><br/>
      Created data:{person.createdData}<br/><br/>
      Permissions:{person.permissions}<br/><br/>
      <Button sx={{padding: 1, marginTop: 2}} variant="outlined" type='submit' state={person} onClick={UpdateUser}>Update</Button>
      <Button sx={{padding: 1, marginTop: 2, marginLeft:2}} variant="outlined" type='submit' onClick={() => navigate(`usersData`)}>Cancel</Button>
    </Container>
  );
}

export default EditUser;
