import { Container } from '@mui/system'
import { Button, AppBar, TextField} from '@mui/material';
import { useState } from 'react';
import {Link, useNavigate} from 'react-router-dom';
import axios from 'axios';
import jwt from 'jsonwebtoken';


function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();


  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/signin', { username, password });
      const { token } = response.data;
      localStorage.setItem('token', token);
      const decodedToken = jwt.decode(token);
      console.log(decodedToken);
      // Do something with the decoded token (e.g., redirect to dashboard)
    } catch (error) {
      console.error(error);
    }



    navigate(`/mainPage`);
  }


  return (
    <Container>
      <AppBar position="static" color='default'>

      </AppBar>
      <h3>Log in Page</h3>
      <TextField label="User name" id="outlined-user-name" onChange={(e) => setUsername(e.target.value)} /><br/><br/>
      <TextField label="Password" id="outlined-user-name" onChange={(e) => setPassword(e.target.value)} />
      <br/>
      <Button sx={{padding: 1, marginTop: 2 }} variant="outlined" size="small" type='submit' color="primary" onClick={handleSubmit}>Login</Button>
      <h3>New User? : {<Link to={`/signUp`} >Create Account</Link>}</h3>
    </Container>
  );
}

export default Login;
