import { Container } from '@mui/system'
import { Button, TextField } from '@mui/material';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'

function AddMember() {
  const navigate = useNavigate();
  const [memberData, setMemberData] = useState({ name: String, email: String, city: String });

  const handleSubmit = async (e) => {
    //Save member in DB 'http://localhost:8000/members'
    const resp = await axios.post('http://localhost:8000/members',memberData)
    window.location.reload();
  }
  return (
    <Container>
      <h3>Add New Member</h3>
      <TextField sx={{ padding: 1, marginTop: 0.5 }} required label="Name" id="outlined-name" onChange={(e) => setMemberData({ ...memberData, name: e.target.value })} /><br />
      <TextField sx={{ padding: 1, marginTop: 0.5 }} required label=" Last Name" id="outlined-last-name" onChange={(e) => setMemberData({ ...memberData, email: e.target.value })} /><br />
      <TextField sx={{ padding: 1, marginTop: 0.5 }} required label=" User Name" id="outlined-last-name" onChange={(e) => setMemberData({ ...memberData, city: e.target.value })} /><br />
      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={handleSubmit}>Save</Button>
      <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/subscriptions/membersData')}>Cancel</Button>


    </Container>
  );
}

export default AddMember;
