import { Container } from '@mui/system'
import { Button, Card, CardMedia, CardContent,List } from '@mui/material';
import { useNavigate, Link } from 'react-router-dom';


function MovieData({ movie }) {
  const navigate = useNavigate();
  const date = new Date(movie.premiered);
  const year = date.getFullYear();
  const genres = movie.genres;
  const genresString = genres.join(", ");

  const DeleteMovie = async (e) => {
    e.preventDefault();
    //delete movie in DB

  }
  return (
    <Container>
      <br />
      <h2>{movie.name} , {year}</h2>
      genres: {genresString}<br /><br />

      <Container style={{ display: "flex" }}>


          <img src={movie.image} alt="pic" />



          <Card sx={{  padding: 1, marginLeft:52,  background: '#eeeeee' }} elevation={3}>
            <CardMedia height="200">Subscriptions watched</CardMedia>
            <CardContent>
            <List sx={{
          width: '100%',
          bgcolor: 'background.paper',
          position: 'relative',
          overflow: 'auto',
          height: 200,
          '& ul': { padding: 0 },
        }}
          subheader={<li />}>
          {

          }
        </List>
            </CardContent>
          </Card>


      </Container>


      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/usersManagement/editUser', { state: { movie } })}>Edit</Button>
      <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={DeleteMovie}>Delete</Button>
    </Container>
  );
}

export default MovieData;
