import { Container } from '@mui/system'
import { Button, TextField } from '@mui/material';
import { useLocation, useNavigate} from 'react-router-dom';
import { useState } from 'react';

function EditUser() {
  const navigate = useNavigate();
  const location = useLocation()
  const person = location.state.person;
  const [userData, setUserData] = useState({ firstName: String(person.firstName), lastName: String(person.lastName) });


  return (
    <Container>
      <br/>
      <h3>Edit User</h3>
      <TextField  sx={{padding: 1, marginTop: 2 }} required label=" First Name" id="outlined-first-name" defaultValue={person.firstName} onChange={(e) => setUserData({ ...userData, firstName: e.target.value })}/><br/>
      <TextField required label=" Last Name" id="outlined-last-name" defaultValue={person.lastName} onChange={(e) => setUserData({ ...userData, lastName: e.target.value })}/>

    </Container>
  );
}

export default EditUser;
