import { Container } from "@mui/system";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate, Outlet } from "react-router-dom";
import {
  AppBar,
  Button,
  IconButton,
  Toolbar,
  Typography,
  Avatar,
  Stack,
} from "@mui/material";
import axios from "axios";

function MainPage() {
  const [user, setUser] = useState({});
  const [isVisible, setIsVisible] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const username = sessionStorage.getItem("username");

  useEffect(() => {
    const UsersData = async () => {
      try {
        const resp = await axios.get(`http://localhost:8080/users/${username}`);
        setUser(resp.data);
        
        if (resp.data.role === "admin") {
          console.log(resp.data);
          const { data: users } = await axios.get('http://localhost:8080/users');
          dispatch({ type: "LOADUSERS", payload: users });
          setIsVisible(true);
        }
      } catch (error) {
        console.error("Error: ", error);
      }
    };
    UsersData();
  }, []);

  const handleSubmit = async (e) => {
    navigate(`/`);
  };

  return (
    <Container>
      <AppBar position="static" color="default">
        <Toolbar>
          <IconButton
            size="small"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 70 }}
            disabled
          >
            <h5>Movies-Subscriptions Web Site</h5>
          </IconButton>
          <Stack direction="row" spacing={2}>
            <Avatar src="/broken-image.jpg" />
            <h3>{user.firstName + " " + user.lastName}</h3>

            <Button
              sx={{ padding: 1, marginTop: 1 }}
              variant="outlined"
              size="small"
              type="submit"
              color="primary"
              onClick={handleSubmit}
            >
              Log Out
            </Button>
          </Stack>
        </Toolbar>
        <Typography
          variant="h6"
          component="div"
          sx={{ flexGrow: 0.1, borderColor: "divider" }}
        >
          <Button onClick={() => navigate(`movies`)}>Movies</Button>
          <Button onClick={() => navigate(`subscriptions`)}>Subscriptions</Button>
          {isVisible && <Button onClick={() => navigate(`usersManagement`)}>Users Management</Button>}
        </Typography>
      </AppBar>

      <Outlet />
    </Container>
  );
}

export default MainPage;
