import { Container } from "@mui/system";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate, Outlet } from "react-router-dom";
import {AppBar,Button,IconButton,Toolbar,Typography,Avatar,Stack,} from "@mui/material";
import axios from "axios";
import { useSelector } from "react-redux";

function MainPage() {
  const username = sessionStorage.getItem("username");
  const allUsers = useSelector((state) => state.users);
  let userOnline = allUsers.filter(person=>person.username===username)
  const [user, setUser] = useState({});

  const [usersManagementVisible, setUsersManagementVisible] = useState(false);
  const [subscriptionsVisible, setSubscriptionsVisible] = useState(false);
  const [moviesVisible, setMoviesVisible] = useState(false);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  
  useEffect(() => {
   
        
        setUser(userOnline);
        if (userOnline.role === "admin") {
          setUsersManagementVisible(true);
          setSubscriptionsVisible(true);
          setMoviesVisible(true);
        }
        else{
        if(userOnline.permissions.includes("View Subscriptions"))
        {
          setSubscriptionsVisible(true);
        }
        if(userOnline.permissions.includes("View Movies"))
        {
          setMoviesVisible(true);
        }
      }
     
  }, []);
  const handleSubmit = async (e) => {
    navigate(`/`);
  };
  return (
    <Container>
      <AppBar position="static" color="default">
        <Toolbar>
          <IconButton size="small" edge="start" color="inherit" aria-label="menu" sx={{ mr: 70 }} disabled >
            <h5>Movies-Subscriptions Web Site</h5>
          </IconButton>
          <Stack direction="row" spacing={2}>
            <Avatar src="/broken-image.jpg" />
            <h3>{user.firstName + " " + user.lastName}</h3>
            <Button sx={{ padding: 1, marginTop: 1 }} variant="outlined" size="small" type="submit" color="primary" onClick={handleSubmit} >Log Out</Button>
          </Stack>
        </Toolbar>
        <Typography variant="h6" component="div" sx={{ flexGrow: 0.1, borderColor: "divider" }} >
          {moviesVisible && <Button onClick={() => navigate(`movies`)}>Movies</Button>}
          {subscriptionsVisible && <Button onClick={() => navigate(`subscriptions`)}>Subscriptions</Button>}
          {usersManagementVisible && <Button onClick={() => navigate(`usersManagement`)}>Users Management</Button>}
        </Typography>
      </AppBar>
      <Outlet />
    </Container>
  );
}
export default MainPage;
