import { Container } from '@mui/system'
import { Button, Checkbox, TextField , FormGroup, FormControlLabel} from '@mui/material';
import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';


function AddUser() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({ firstName: String, lastName: String, username: String, sessionTimeOut: Number, createdData: new Date().toLocaleDateString('en-CA'), permissions: [] });

  const handleSubmit = async (e) => {
    e.preventDefault();


  }

  return (
    <Container>
      <h3>Add New Users</h3>
      <TextField sx={{padding: 1, marginTop: 0.5}} required label=" First Name" id="outlined-first-name" onChange={(e) => setUserData({ ...userData, firstName: e.target.value })} /><br/>
      <TextField sx={{padding: 1, marginTop: 0.5}} required label=" Last Name" id="outlined-last-name" onChange={(e) => setUserData({ ...userData, lastName: e.target.value })} /><br/>
      <TextField sx={{padding: 1, marginTop: 0.5}} required label=" User Name" id="outlined-last-name" onChange={(e) => setUserData({ ...userData, username: e.target.value })} /><br/>
      <TextField sx={{padding: 1, marginTop: 0.5}} required label="Session Time Out (minutes)" id="outlined-session-time-out" onChange={(e) => setUserData({ ...userData, sessionTimeOut: e.target.value })} /><br/>
      <FormGroup>
      <FormControlLabel control={<Checkbox defaultChecked />} label="View Subscriptions" onChange={(e)=>{setUserData(...userData,)}} />
      <FormControlLabel  control={<Checkbox />} label="Create Subscriptions" />
      <FormControlLabel  control={<Checkbox />} label="Delete Subscriptions" />
      <FormControlLabel  control={<Checkbox />} label="Update Subscriptions" />
      <FormControlLabel control={<Checkbox defaultChecked />} label="View Movies" />
      <FormControlLabel  control={<Checkbox />} label="Create Movies" />
      <FormControlLabel  control={<Checkbox />} label="Delete Movies" />
      <FormControlLabel  control={<Checkbox />} label="Update Movies" />
      <Button sx={{padding: 1, marginTop: 2 }}size="small" type='submit' color="primary" onClick={handleSubmit}>Save</Button>
      <Button sx={{padding: 1, marginTop: 2}} onClick={() => navigate(`usersData`)}>Cancel</Button>

      </FormGroup>


   </Container>
  );
}

export default AddUser;
