import { Container } from '@mui/system'
import { Button, FormControl, InputLabel, Select, OutlinedInput, MenuItem } from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import { useState } from 'react';
import { useSelector } from 'react-redux';

function SubscribeToNewMovie() {
  const location = useLocation()
  const member = location.state.member;
  const movies = useSelector((state) => state.movies);
  const [movieData, setMovieData] = useState({ memberId: member._id, movies: [{ movieId: String, date: Date }] });
  const [selectedDate, setSelectedDate] = useState("");

  const handleMovieDataUpdate = (movieId, date) => {
    const updatedMovieData = { ...movieData };
    updatedMovieData.movies.push({ movieId, date });
    setMovieData(updatedMovieData);
  }




  const handleSubmit = async (e) => {
    e.preventDefault();
    //Save movie to member subscriptions with member_ID in DB and make MSG
    //setSubscriptionData({ ...subscriptionData, movies: [...subscriptionData.movies,movieData] })

    console.log(movieData)

  }
  return (
    <Container>
      <br />
      <h3>Add New Movie</h3>
      <input style={{ padding: 18.5, marginTop: 8, width: 260, borderColor: 'lightgrey ', borderWidth: 1, borderRadius: 5 }} defaultValue=' ' type='date' name='date' onChange={(e) => setSelectedDate(e.target.value)} /><br /><br />
      <FormControl required sx={{ m: 1, width: 300, }}>
        <InputLabel id="demo-simple-select-required-label" >Movies</InputLabel>
        <Select labelId="demo-multiple-movies-label" id="demo-multiple-movies"
          defaultValue='' name='movieId' onChange={(e)=>handleMovieDataUpdate(e.target.value, selectedDate)} input={<OutlinedInput label="Movies" />}>
          {
            movies.map(movie => {
              return <MenuItem value={movie._id} key={movie._id}>{movie.name}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      <br />

      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={handleSubmit}>Subscribe</Button>

    </Container>
  );
}

export default SubscribeToNewMovie;
