import { Container } from '@mui/system'
import { Button, Card, CardMedia, CardContent, List } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'

function MovieData({ movie }) {
  const navigate = useNavigate();
  const date = new Date(movie.premiered);
  const year = date.getFullYear();
  const genres = movie.genres;
  const genresString = genres.join(", ");

  const DeleteMovie = async (e) => {
     //delete in DB 'http://localhost:8000/movies'
     await axios.delete(`${'http://localhost:8000/movies'}/${movie._id}`);
     window.location.reload();
     //DELETE FROM SUBSCRIPTIONS !!!!!!!
  }
  return (
    <Container>
      <br />
      <Card sx={{ padding: 1, marginLeft: 2, background: '#eeeeee' }} elevation={3}>

        <h2 style={{ marginLeft: 30 }}>{movie.name} , {year}</h2>

        <h4 style={{ marginLeft: 30 }}>genres: {genresString}</h4><br />
        <Container style={{ display: "flex" }}>
          <img src={movie.image} alt="pic" />
          <Card sx={{ padding: 1, marginLeft: 2, background: '#eeeeee', width: 500 }} elevation={3}>
            <CardMedia height="200">Subscriptions watched</CardMedia>
            <CardContent>
              <List sx={{
                width: '100%',
                position: 'relative',
                overflow: 'auto',
                height: 190,
                '& ul': { padding: 0 },
              }}
                subheader={<li />}>
                {
                  <h5>Here make a list</h5>
                }
              </List>
            </CardContent>
          </Card>

        </Container>

        <Button sx={{ padding: 1, marginTop: 2, marginLeft: 3 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/editMovie', { state: { movie } })}>Edit</Button>
        <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={DeleteMovie}>Delete</Button>
      </Card>
    </Container>
  );
}

export default MovieData;
