import { Container } from '@mui/system'
import { Button, FormControl, InputLabel, Select, OutlinedInput, MenuItem } from '@mui/material';
import { useLocation } from 'react-router-dom';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios'

function SubscribeToNewMovie() {
  const location = useLocation()
  const member = location.state.member;
  const movies = useSelector((state) => state.movies);
  const [subscriptionData, setSubscriptionData] = useState({ memberId: member._id, movies:[] });
  const [movieData, setMovieData] = useState([ { movieId: '', date: '' }]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    subscriptionData.movies=movieData;
    //Save movie  to member subscriptions in DB 'http://localhost:8000/subscriptions'
    const resp = await axios.post('http://localhost:8000/subscriptions',movieData)
    window.location.reload();

    console.log(subscriptionData)
  }

  return (
    <Container>
      <br />
      <h3>Add New Movie</h3>

      <FormControl required sx={{ m: 1, width: 300, }}>
        <InputLabel id="demo-simple-select-required-label" >Movies</InputLabel>
        <Select labelId="demo-multiple-movies-label" id="demo-multiple-movies"
          defaultValue='' name='movieId' onChange={(e) => setMovieData({ ...movieData, movieId: e.target.value })} input={<OutlinedInput label="Movies" />}>
          {
            movies.map(movie => {
              return <MenuItem value={movie._id} key={movie._id}>{movie.name}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      <br />
      <input style={{ padding: 18.5, marginTop: 8, width: 260, borderColor: 'lightgrey ', borderWidth: 1, borderRadius: 5 }} defaultValue=' ' type='date' name='date'  onChange={(e) => setMovieData({ ...movieData, date: e.target.value })} /><br /><br />
      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={handleSubmit}>Subscribe</Button>

    </Container>
  );
}

export default SubscribeToNewMovie;
