import { Container } from '@mui/system'
import { Button , Grid, Card} from '@mui/material';
import { useEffect } from 'react';
import { useNavigate, Outlet} from 'react-router-dom';
import { useSelector } from "react-redux";
import axios from 'axios'

function Subscriptions() {
  const navigate = useNavigate();
  const subscriptions = useSelector((state) => state.subscriptions);
  const members = useSelector((state) => state.members);
  const DeleteUser = async (e) => {
    //delete in DB 'http://localhost:8000/members'
    const resp = await axios.delete(`${'http://localhost:8000/members'}/${member._id}`);
    window.location.reload();
  }
  useEffect(() => {
    navigate('membersData')
  }, [])

  return (
    <Container>
      <h3>Subscriptions</h3>
      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" size="small" type='submit' color="primary" onClick={() => navigate(`membersData`)}>All Members</Button>
      <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type='submit' color="primary" onClick={() => navigate(`addNewMember`)}>Add Member</Button>
      <Container sx={{ marginTop: 5 }}>
      <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
        {
          members.map((member) => {
            return (
              <Card sx={{ width: 500, padding: 1, margin: 2, background: '#eeeeee' }} elevation={3}>
                <h2>{member.name}</h2>

                Email: {member.email}<br /><br />
                City:{member.city}<br /><br />
                <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/subscriptions/editMember', { state: { member } })}>Edit</Button>
                <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={DeleteUser}>Delete</Button>

                <Card sx={{ width: 450, padding: 1, margin: 2, background: '#eeeefff' }} elevation={3}>
                  <h4>Movies watched</h4>
                  <Button sx={{ padding: 1 }} size='small' variant="outlined" type='submit' onClick={() => navigate('subscribeToNewMovie', { state: { member } })}>Subscribe to new movie</Button>
                  <br /><br />
                  <Outlet />
                  *list of watched movies
                </Card>
              </Card>
            );
          })
        }
      </Grid>
    </Container>
    </Container>
  );
}

export default Subscriptions;
