import { Container } from '@mui/system'
import { Button, TextField } from '@mui/material';
import { useLocation, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios'

function EditMovie() {
  const navigate = useNavigate();
  const location = useLocation()
  const movie = location.state.movie;
  const genres = movie.genres;
  const genresString = genres.join(", ");
  const datePremiered = new Date(movie.premiered);
  const year = datePremiered.getFullYear();

  const [movieData, setMovieData] = useState({ name: String(movie.name), genres: String(genresString), imageUrl: String(movie.image), premiered: String(year) });

  const handleSubmit = async (e) => {
    e.preventDefault();
    //Update movie in DB 'http://localhost:8000/movies'
    const resp = await axios.post('http://localhost:8000/movies',movieData)
    window.location.reload();
  }

  return (
    <Container>
      <br />
      <h3>Edit Movie</h3>
      <TextField sx={{ padding: 1, marginTop: 2, width: 300 }} required label=" Movie Name" id="outlined-movie-name" defaultValue={movie.name} onChange={(e) => setMovieData({ ...movieData, name: e.target.value })} /><br />
      <TextField sx={{ padding: 1, marginTop: 2, width: 300 }} required label=" Genres" id="outlined-genres" defaultValue={genresString} onChange={(e) => setMovieData({ ...movieData, genres: e.target.value })} /><br />
      <TextField sx={{ padding: 1, marginTop: 2, width: 300 }} required label=" Image URL" id="outlined-image-url" defaultValue={movie.image} onChange={(e) => setMovieData({ ...movieData, imageUrl: e.target.value })} /><br />
      <TextField sx={{ padding: 1, marginTop: 2, width: 300 }} required label=" Premiered" id="outlined-premiered" defaultValue={year} onChange={(e) => setMovieData({ ...movieData, premiered: e.target.value })} /><br />

      <Button sx={{ padding: 1, marginTop: 2 }} variant="outlined" type='submit' onClick={handleSubmit}>Update</Button>
      <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/movies/moviesData')}>Cancel</Button>
    </Container>
  );
}

export default EditMovie;
