import { Container } from "@mui/system";
import { Button, Card, CardMedia, CardContent, List, Grid, Typography, ListItem, TextField } from "@mui/material";
import { useState, useEffect } from "react";
import { useNavigate, useLocation, Link} from "react-router-dom";
import { useSelector } from "react-redux";
import axios from 'axios'


function Movies() {
  console.log('Movies update')
  const navigate = useNavigate();
  const location = useLocation()

  const allMembers = sessionStorage.getItem('members');
  const members = JSON.parse(allMembers);

  const allSubscriptions = sessionStorage.getItem('subscriptions');
  const subscriptions = JSON.parse(allSubscriptions);

  const allMovies = sessionStorage.getItem('movies');
  const movies = JSON.parse(allMovies);

  const [moviesToShow, setMoviesToShow] = useState(movies);
  const [findMovie, setFindMovie] = useState("");

  const showName  = location.state;
  const userOnline = sessionStorage.getItem('userOnline');
  const user = JSON.parse(userOnline);


    useEffect(() => {
    try {
  //find by Name if get from membersData componet
  if (showName) {
    const searchString = showName.trim();
    const filteredMovies = movies.filter((mov) => mov.name.toLowerCase().includes(searchString.toLowerCase()));
    setMoviesToShow(filteredMovies); // update state with filtered movies
    console.log(showName)
  }
  else{
    setMoviesToShow(movies)
  }
} catch (error) {
  console.log(error);
}
// eslint-disable-next-line react-hooks/exhaustive-deps
}, [])  

const DeleteMovie = async ( movID) => {
//delete in DB 'http://localhost:8000/movies'  and  'http://localhost:8000/subscriptions'
await axios.delete(`${'http://localhost:8000/movies'}/${movID}`);
await axios.put(`${'http://localhost:8000/subscriptions'}/${movID}`,movID) 
}
 const FindMovie = (e) => {
  // find movie and display
  try {
    if (findMovie) {
      const searchString = findMovie.trim();
      const filteredMovies = movies.filter((mov) => mov.name.toLowerCase().includes(searchString.toLowerCase()));
      setMoviesToShow(filteredMovies); // update state with filtered movies
    } else {
      setMoviesToShow(movies); // update state with all movies
    }
  } catch (error) {
    console.log(error);
  }
};
const AllMoviesShow = () => {
  setMoviesToShow(movies);
}; 

  return (
    <Container>
      <h1 style={{ textAlign: 'center', fontFamily: 'Comic Sans MS', fontSize: '3rem', color: '#4a6baf' }}>Movies</h1>
      <Button sx={{ padding: 2, marginTop: 2 }} variant="outlined" size="small" type="submit" color="primary"
        onClick={AllMoviesShow}>All Movies</Button>
     {user.permissions.includes('Create Movies') && <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type="submit" color="primary"
        onClick={() => navigate(`/mainPage/addNewMovie`)}>Add Movie</Button>}
      <TextField sx={{ padding: 1, marginTop: 1, marginLeft: 1 }} required label="Find Movie" id="outlined-find-movie"
        onChange={(e) => setFindMovie(e.target.value)} />
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type="submit" color="primary"
        onClick={FindMovie}>{" "}Find{" "}</Button>
      <br />
      <ul>
        {
          console.log('movies'+movies)
        }
      </ul>
    </Container>
  );
}
export default Movies;
