import { Container } from "@mui/system";
import { Button, TextField, Card, CardMedia, CardContent, List, Grid, Typography, ListItem } from "@mui/material";
import { useState, useEffect } from "react";
import { useNavigate, Outlet, useLocation, Link } from "react-router-dom";
import { useSelector, useDispatch  } from "react-redux";
import axios from 'axios'

function Movies() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation()
  const members = useSelector((state) => state.members);
  const subscriptions = useSelector((state) => state.subscriptions);
  const movies = useSelector((state) => state.movies);
  const [findMovie, setFindMovie] = useState("");
  const [moviesToShow, setMoviesToShow] = useState(movies);
  const movieName = location.state;

  useEffect(() => {
    try {
      if (movieName) {
        const searchString = movieName.trim();
        const filteredMovies = movies.filter((mov) => mov.name.toLowerCase().includes(searchString.toLowerCase()));
        setMoviesToShow(filteredMovies); // update state with filtered movies
      } else {
        setMoviesToShow(movies); // update state with all movies
      }
    } catch (error) {
      console.log(error);
    }
  }, [])

  const DeleteMovie = async (movID) => {
    //delete in DB 'http://localhost:8000/movies'  and  'http://localhost:8000/subscriptions'
    await axios.delete(`${'http://localhost:8000/movies'}/${movID}`);
    await axios.put(`${'http://localhost:8000/subscriptions'}/${movID}`,movID)
    dispatch({ type: 'DELETE_MOVIE', payload: movID });


  }
  const FindMovie = (e) => {
    // find movie and display
    try {
      if (findMovie) {
        const searchString = findMovie.trim();
        const filteredMovies = movies.filter((mov) => mov.name.toLowerCase().includes(searchString.toLowerCase()));
        setMoviesToShow(filteredMovies); // update state with filtered movies
      } else {
        setMoviesToShow(movies); // update state with all movies
      }
    } catch (error) {
      console.log(error);
    }
  };
  const AllMoviesShow = () => {
    setMoviesToShow(movies);
  };
  return (
    <Container>
      <h3>MOVIES</h3>
      <Button sx={{ padding: 2, marginTop: 2 }} variant="outlined" size="small" type="submit" color="primary"
        onClick={AllMoviesShow}>All Movies</Button>
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type="submit" color="primary"
        onClick={() => navigate(`/mainPage/addNewMovie`)}>Add Movie</Button>
      <TextField sx={{ padding: 1, marginTop: 1, marginLeft: 1 }} required label="Find Movie" id="outlined-find-movie"
        onChange={(e) => setFindMovie(e.target.value)} />
      <Button sx={{ padding: 2, marginTop: 2, marginLeft: 2 }} variant="outlined" size="small" type="submit" color="primary"
        onClick={FindMovie}>{" "}Find{" "}</Button>
      <br />
      <ul>
        {
          moviesToShow.map((movie) => {
            return <div key={movie._id}>
              <Card sx={{ padding: 1, marginLeft: 2, background: '#eeeeee' }} elevation={3}>
                <li> {movie.name}, {new Date(movie.premiered).getFullYear()}</li><br />
                <li>genres:  {movie.genres.join(", ")}</li>
                <Container style={{ display: "flex" }}>
                  {<img src={movie.image} alt="pic" />}
                  <Card sx={{ padding: 1, marginLeft: 2, background: '#eeeeee', width: 500 }} elevation={3}>
                    <CardMedia height="200">Subscriptions watched</CardMedia>
                    <CardContent>
                      {subscriptions.map(sub => {
                        return <div key={sub._id}>
                          {
                            sub.movies.filter(mov => mov.movieId === movie._id).map(item => {
                              return <div key={movie._id}>
                                {
                                  members.filter(member => member._id === sub.memberId).map(member => {
                                    return <div key={member._id}>
                                      {
                                        <List sx={{ width: '100%', position: 'relative', overflow: 'auto', height: 190, '& ul': { padding: 0 }, }} subheader={<li />}>
                                        {
                                          <ListItem key={`item-${member._id}-${member._id}`}>
                                          <Grid item xs>
                                            <Typography gutterBottom variant="h6" component="div">
                                              <Link to={"/mainPage/subscriptions/membersData"} state={member.name} >{member.name}</Link>
                                            </Typography>
                                          </Grid>
                                          <Grid item xs>
                                            <Typography gutterBottom variant="h6" component="div">
                                              <h5>{new Date(item.date).toLocaleDateString("en-CA", { year: "numeric", month: "2-digit", day: "2-digit", })}</h5>
                                            </Typography>
                                          </Grid>
                                        </ListItem>
                                        }
                                      </List>
                                      }
                                    </div>
                                  })
                                }
                              </div>
                            })
                          }
                        </div>
                      })
                      }
                    </CardContent>
                  </Card>
                </Container>
                <Button sx={{ padding: 1, marginTop: 2, marginLeft: 3 }} variant="outlined" type='submit' onClick={() => navigate('/mainPage/editMovie', { state: { movie } })}>Edit</Button>
                <Button sx={{ padding: 1, marginTop: 2, marginLeft: 2 }} variant="outlined" type='submit' onClick={() => DeleteMovie(movie._id)}>Delete</Button>
              </Card><br />
            </div>
          })
        }
      </ul>
      <Outlet />
    </Container>
  );
}
export default Movies;
